var contactform = $("#inquiry-form");
var contactformsubmitbtn = $("#form-submit-btn");
var contacformalert = $("#form-ajax-result");

 
!function ($) {
  $(function(){
    $('#main').smoothState();  
     $("img.lazy").lazyload({threshold : 200,effect : "fadeIn"});
  
  
       //contactform[0].reset();
      $(document).on({
      click: function(e) {
        
          e.preventDefault();
          if(contactform.parsley('validate')){
              
              $("#form-submit-btn").fadeOut("fast");
              $("#form-ajax-result").removeClass('alert-success alert-error').addClass('alert-warning').html("Submitting Form, Please Wait...").show();
            
              $.post(base_url+"en/ajax_general_inquiry_form/index", contactform.serialize(),
              function( data ) {
                  if(data.status=="error"){
                    contacformalert.removeClass('alert-warning alert-success').addClass('alert-danger').html(data.message);
                    $("#security_code").addClass("parsley-error");
                     contactformsubmitbtn.fadeIn("fast");
             
                  }
                  else if(data.status=="success"){
                    contacformalert.removeClass('alert-warning alert-danger').addClass('alert-success').html(data.message);
                    contactform[0].reset();
                  }
              },"json");
           }    

      }
    }, "#form-submit-btn" );
        
        
        
        
    
    if(controller=="home"){
      $('.home-initial-carousel').carousel({
        interval: 5000
      })
  

      $('.portfolio-carousel').carousel({
        interval: 10000, pause:"hover"
      });
      
      $('.portfolio-carousel').on('slide.bs.carousel', function() {
          var $nextImage = $('.active.item', this).next('.item').find('img');
          $nextImage.attr('src', $nextImage.data('lazy-load-src'));
      });
      
    }
    
    if(controller=="services" && sub_controller!=""){
      $('html, body').animate({scrollTop: $("#"+sub_controller).offset().top - 51 }, 1500);
    }
    

    
    $('[data-toggle="popover"]').popover({trigger: 'hover','placement': 'left',animation:true,html:true});
  
    $.scrollDepth();
    
    jQuery(window).scroll(function () {
      var top = jQuery(document).scrollTop();

      
      if (top > 30) {
        jQuery('.main-menu').addClass('tiny');
      } else {
       jQuery('.main-menu').removeClass('tiny'); 
      }
    });


  });
}(window.jQuery);  



  

